// Statistics

function calculateWMA(source, rmas, periods) {  // Having a function avoids us to repeat code.
    /* We check we have enough values to make the calculation */
    if (source.length < periods) { return 0 } // If we dont, we define the value is zero.

    let alpha = 1 / periods
    let avg = source[source.length - 1] * alpha + rmas[rmas.length - 1] * (1 - alpha)
    return avg;
}

function calculateRMAU(periods) {
    return calculateWMA(variable.last14U, variable.rma14U, periods)
}

function calculateRMAD(periods) {
    return calculateWMA(variable.last14D, variable.rma14D, periods)
}

