let candle = record.current // This is the primary dependency
let volume = getElement('volumes', candle) // Here I get the volume bar corresponding to the current candle
let totalVolume = volume.buy + volume.sell // Candles Volumes split the volume received from the exchange evenly betwen buy and sell because it does not know how much of it there is.

let hlc3 = (candle.max + candle.min + candle.close) / 3
let lastHlc3 = variable.lastHlc3

if (record.previous !== undefined) {

    let change = hlc3 - variable.lastHlc3

    variable.last14U.push(totalVolume * (change <= 0 ? 0 : hlc3))
    variable.last14D.push(totalVolume * (change >= 0 ? 0 : hlc3))

    if (variable.last14U.length > 14) {
        variable.last14U.splice(0, 1) // Remove the first element of the array to keep it at a maximun of 14 elements.
    }

    if (variable.last14D.length > 14) {
        variable.last14D.splice(0, 1) // Remove the first element of the array to keep it at a maximun of 14 elements.
    }

    let upper = variable.last14U.reduce(function(a, b){return a + b;}, 0);
    let lower = variable.last14D.reduce(function(a, b){return a + b;}, 0);

    let mfi

    if(lower == 0)
        mfi = 100
    else if(upper == 0)
        mfi = 0
    else  // RSI
        mfi = 100 - (100 / (1 + upper / lower))

    if (mfi !== undefined) {
        variable.value = mfi.toFixed(2)
    } else {
        variable.value = 0
    }

